     1â†’/**
     2â†’ * Character.js â€” tile-based character with A* pathfinding and state machine.
     3â†’ * Pure JS, no Phaser dependency. Sprite/label references are set externally.
     4â†’ */
     5â†’
     6â†’const TILE_SIZE = 48;
     7â†’
     8â†’// â”€â”€â”€ A* pathfinding â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
     9â†’
    10â†’function heuristic(ax, ay, bx, by) {
    11â†’  return Math.abs(ax - bx) + Math.abs(ay - by);
    12â†’}
    13â†’
    14â†’/**
    15â†’ * Find a path from (startX, startY) to (goalX, goalY) on a walkable grid.
    16â†’ * @param {boolean[][]} walkable  - walkable[row][col]
    17â†’ * @param {number} cols
    18â†’ * @param {number} rows
    19â†’ * @returns {Array<{tx,ty}>|null}  - list of steps NOT including start; null if no path
    20â†’ */
    21â†’function findPath(walkable, cols, rows, startX, startY, goalX, goalY) {
    22â†’  if (startX === goalX && startY === goalY) return [];
    23â†’  if (!walkable[startY]?.[startX] || !walkable[goalY]?.[goalX]) return null;
    24â†’
    25â†’  const key = (x, y) => x * 1000 + y;
    26â†’
    27â†’  const open = new Map();
    28â†’  const closed = new Set();
    29â†’
    30â†’  const startNode = { x: startX, y: startY, g: 0, h: heuristic(startX, startY, goalX, goalY), parent: null };
    31â†’  open.set(key(startX, startY), startNode);
    32â†’
    33â†’  while (open.size > 0) {
    34â†’    // Pick node with lowest f = g + h
    35â†’    let current = null;
    36â†’    for (const node of open.values()) {
    37â†’      if (!current || node.g + node.h < current.g + current.h) current = node;
    38â†’    }
    39â†’
    40â†’    if (current.x === goalX && current.y === goalY) {
    41â†’      // Reconstruct path
    42â†’      const path = [];
    43â†’      let node = current;
    44â†’      while (node.parent) {
    45â†’        path.unshift({ tx: node.x, ty: node.y });
    46â†’        node = node.parent;
    47â†’      }
    48â†’      return path;
    49â†’    }
    50â†’
    51â†’    open.delete(key(current.x, current.y));
    52â†’    closed.add(key(current.x, current.y));
    53â†’
    54â†’    const neighbors = [
    55â†’      { x: current.x + 1, y: current.y },
    56â†’      { x: current.x - 1, y: current.y },
    57â†’      { x: current.x,     y: current.y + 1 },
    58â†’      { x: current.x,     y: current.y - 1 },
    59â†’    ];
    60â†’
    61â†’    for (const n of neighbors) {
    62â†’      if (n.x < 0 || n.x >= cols || n.y < 0 || n.y >= rows) continue;
    63â†’      if (!walkable[n.y]?.[n.x]) continue;
    64â†’      if (closed.has(key(n.x, n.y))) continue;
    65â†’
    66â†’      const g = current.g + 1;
    67â†’      const existing = open.get(key(n.x, n.y));
    68â†’      if (existing) {
    69â†’        if (g < existing.g) {
    70â†’          existing.g = g;
    71â†’          existing.parent = current;
    72â†’        }
    73â†’      } else {
    74â†’        open.set(key(n.x, n.y), {
    75â†’          x: n.x, y: n.y,
    76â†’          g, h: heuristic(n.x, n.y, goalX, goalY),
    77â†’          parent: current,
    78â†’        });
    79â†’      }
    80â†’    }
    81â†’  }
    82â†’
    83â†’  return null; // no path
    84â†’}
    85â†’
    86â†’// â”€â”€â”€ Character â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
    87â†’
    88â†’export class Character {
    89â†’  /**
    90â†’   * @param {object} config
    91â†’   * @param {string}   config.name        - sprite key prefix (e.g. 'Amelia')
    92â†’   * @param {string}   config.label       - display name (e.g. 'Maple')
    93â†’   * @param {number}   config.tx          - starting tile X
    94â†’   * @param {number}   config.ty          - starting tile Y
    95â†’   * @param {boolean[][]} config.walkable - walkable grid [row][col]
    96â†’   * @param {number}   config.gridCols
    97â†’   * @param {number}   config.gridRows
    98â†’   * @param {object}   [config.sprite]    - Phaser sprite (set after construction)
    99â†’   * @param {object}   [config.nameLabel] - Phaser Text object
   100â†’   * @param {number}   [config.moveSpeed] - tiles per second (default 2)
   101â†’   */
   102â†’  constructor(config) {
   103â†’    this.name      = config.name;
   104â†’    this.label     = config.label;
   105â†’    this.sprite    = config.sprite    || null;
   106â†’    this.nameLabel = config.nameLabel || null;
   107â†’
   108â†’    this.tx     = config.tx;
   109â†’    this.ty     = config.ty;
   110â†’    this.prevTx = config.tx;
   111â†’    this.prevTy = config.ty;
   112â†’
   113â†’    this.path        = [];
   114â†’    this.moveProgress = 0;
   115â†’    this.moveSpeed   = config.moveSpeed || 2; // tiles/sec
   116â†’
   117â†’    // State: 'idle' | 'walking' | 'interacting'
   118â†’    this.action = 'idle';
   119â†’    this.facing = 'down';
   120â†’
   121â†’    // Wander: timer counts DOWN; engine starts wander when <= 0
   122â†’    this.wanderTimer = 3000 + Math.random() * 5000; // 3â€“8 s
   123â†’
   124â†’    // Interaction
   125â†’    this.interactTimer  = 0;
   126â†’    this.interactTarget = null;
   127â†’
   128â†’    // Personality stats (0â€“100)
   129â†’    this.mood   = 100;
   130â†’    this.energy = 100;
   131â†’
   132â†’    // Optional dialogue driven by AI
   133â†’    this.currentDialogue = null;
   134â†’    this.dialogueTimer   = 0;
   135â†’
   136â†’    // Preset dialogue lines (used for random speech bubbles)
   137â†’    this.dialogue     = config.dialogue || [];
   138â†’    // Counts down; when it hits 0 Engine sets pendingSpeech
   139â†’    this.speechTimer  = 20000 + Math.random() * 20000; // 20â€“40 s
   140â†’    // Scene reads this each frame and shows a bubble, then clears it
   141â†’    this.pendingSpeech = null;
   142â†’
   143â†’    this.walkable  = config.walkable;
   144â†’    this.gridCols  = config.gridCols;
   145â†’    this.gridRows  = config.gridRows;
   146â†’
   147â†’    // Object interaction targeting â€” set by engine, cleared on arrival
   148â†’    this.targetObjectId = null;
   149â†’    this.pendingAction  = null;
   150â†’  }
