     1→export type Goal = 'weight_loss' | 'muscle_gain' | 'endurance' | 'flexibility' | 'general_fitness';
     2→export type Style = 'high_intensity' | 'low_impact' | 'strength' | 'cardio' | 'yoga';
     3→export type FeedbackStyle = 'detailed' | 'brief' | 'motivational' | 'technical';
     4→export type Structure = 'strict' | 'flexible' | 'mixed';
     5→export type CheckinFrequency = 'daily' | 'weekly' | 'biweekly' | 'monthly';
     6→
     7→export interface TrainerProfile {
     8→  id: string;
     9→  name: string;
    10→  goal: Goal;
    11→  style: Style;
    12→  feedback: FeedbackStyle;
    13→  structure: Structure;
    14→  checkins: CheckinFrequency;
    15→  area: string;
    16→  specialty: string;
    17→  website: string;
    18→}
    19→
    20→export interface UserPreferences {
    21→  goal: Goal;
    22→  style: Style;
    23→  feedback: FeedbackStyle;
    24→  structure: Structure;
    25→  checkins: CheckinFrequency;
    26→}
    27→
    28→const WEIGHTS = {
    29→  goal: 30,
    30→  style: 20,
    31→  feedback: 20,
    32→  structure: 20,
    33→  checkins: 10,
    34→} as const;
    35→
    36→export const MATCH_THRESHOLD = 60;
    37→
    38→/**
    39→ * Calculates a compatibility score (0–100) between a trainer and user preferences.
    40→ * Each attribute contributes its weight when trainer and user values match.
    41→ */
    42→export function calculateScore(trainer: TrainerProfile, user: UserPreferences): number {
    43→  let score = 0;
    44→  if (trainer.goal === user.goal) score += WEIGHTS.goal;
    45→  if (trainer.style === user.style) score += WEIGHTS.style;
    46→  if (trainer.feedback === user.feedback) score += WEIGHTS.feedback;
    47→  if (trainer.structure === user.structure) score += WEIGHTS.structure;
    48→  if (trainer.checkins === user.checkins) score += WEIGHTS.checkins;
    49→  return score;
    50→}
    51→
    52→/**
    53→ * Returns a human-readable label for a compatibility score.
    54→ * Scores >= 80 are "Great Match", >= 60 (threshold) are "Good Match", below is "Poor Match".
    55→ */
    56→export function scoreLabel(score: number): string {
    57→  if (score >= 80) return 'Great Match';
    58→  if (score >= MATCH_THRESHOLD) return 'Good Match';
    59→  return 'Poor Match';
    60→}
