    50→
    51→  test('should have debug information section', async ({ page }) => {
    52→    await page.waitForLoadState('networkidle');
    53→
    54→    // Verify Debug Information section exists
    55→    const debugHeading = page.getByRole('heading', { name: /Debug Information/i });
    56→    await expect(debugHeading).toBeVisible();
    57→
    58→    // Verify Template System subsection
    59→    const templateSystemHeading = page.getByRole('heading', { name: /Template System/i });
    60→    await expect(templateSystemHeading).toBeVisible();
    61→
    62→    // Verify active template is shown
