    68→
    69→  test('should display sample data structure in debug section', async ({ page }) => {
    70→    await page.waitForLoadState('networkidle');
    71→
    72→    // Verify Sample Data Structure subsection
    73→    const sampleDataHeading = page.getByRole('heading', { name: /Sample Data Structure/i });
    74→    await expect(sampleDataHeading).toBeVisible();
    75→
    76→    // Verify JSON data is displayed
    77→    const jsonBlock = page.locator('pre.code-block');
    78→    await expect(jsonBlock).toBeVisible();
    79→
