import { test, expect } from '@playwright/test';

/**
 * Main App QA Tests
 *
 * Tests covering the five key scenario areas:
 * 1. Welcome / Onboarding Screen
 * 2. Template Switcher
 * 3. Document Type Selector
 * 4. Core Editor Flow
 * 5. Existing functionality (no errors, responsive layout)
 *
 * QA findings (bugs discovered against deployed app):
 * - BUG: Both USelectMenu dropdowns (Type, Template) open but render
 *   "No data" with no selectable options. Root cause: @nuxt/ui v4 renamed
 *   the prop from `options` to `items`; AppHeader still passes `options`.
 *   Workaround: set localStorage values directly then reload.
 */

// ─── helpers ─────────────────────────────────────────────────────────────────

/** Clear all ohmydoc localStorage keys so the page starts fresh. */
async function clearStorage(page: Parameters<typeof test>[1] extends (...args: infer P) => void ? P[0] : never) {
  await page.evaluate(() => {
    localStorage.removeItem('ohmydoc_xml_content');
    localStorage.removeItem('ohmydoc_template');
    localStorage.removeItem('ohmydoc_document_type');
  });
}

// ─── 1. Welcome / Onboarding Screen ──────────────────────────────────────────

test.describe('Welcome / Onboarding Screen', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await clearStorage(page);
    await page.reload();
  });

  test('shows welcome screen on first visit (no localStorage)', async ({ page }) => {
    await expect(page.getByRole('heading', { level: 2 })).toHaveText(
      'Create professional documents in seconds',
    );
    await expect(
      page.getByText(
        'Write structured XML, pick a template, and instantly preview a polished document — ready to export.',
      ),
    ).toBeVisible();
    await expect(page.getByRole('button', { name: 'Start with a sample' })).toBeVisible();
  });

  test('welcome screen does NOT show the editor panels', async ({ page }) => {
    // Monaco editor textbox must be absent on welcome screen
    await expect(page.getByRole('textbox', { name: 'Editor content' })).not.toBeVisible();
  });

  test('"Start with a sample" loads cover-letter XML and shows the editor', async ({ page }) => {
    await page.getByRole('button', { name: 'Start with a sample' }).click();

    // Welcome heading must disappear
    await expect(
      page.getByRole('heading', { name: 'Create professional documents in seconds' }),
    ).not.toBeVisible();

    // Monaco editor must now be visible
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
  });

  test('"Start with a sample" renders the cover-letter preview', async ({ page }) => {
    await page.getByRole('button', { name: 'Start with a sample' }).click();

    // Wait for preview content to appear
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    await expect(page.getByText('Brightwave Marketing')).toBeVisible();
    await expect(page.getByText('Dear Hiring Manager,')).toBeVisible();
  });

  test('reloading after "Start with a sample" skips the welcome screen', async ({ page }) => {
    // Click to load sample (this persists XML to localStorage)
    await page.getByRole('button', { name: 'Start with a sample' }).click();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();

    // Reload — welcome screen must NOT reappear
    await page.reload();
    await expect(
      page.getByRole('heading', { name: 'Create professional documents in seconds' }),
    ).not.toBeVisible();

    // Editor and preview must still be visible
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
  });
});

// ─── 2. Template Switcher ─────────────────────────────────────────────────────

test.describe('Template Switcher', () => {
  test.beforeEach(async ({ page }) => {
    // Start from a state with sample XML loaded and default template
    await page.goto('/');
    await page.evaluate(() => {
      localStorage.removeItem('ohmydoc_template');
    });
    await page.reload();
    // Load sample if on welcome screen
    const startBtn = page.getByRole('button', { name: 'Start with a sample' });
    if (await startBtn.isVisible()) {
      await startBtn.click();
      await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    }
  });

  test('header shows "Template" label with current template button', async ({ page }) => {
    await expect(page.getByText('Template')).toBeVisible();
    // Default template is "modern"
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toBeVisible();
    await expect(templateBtn).toContainText('modern');
  });

  test('clicking Template button opens a dropdown popup', async ({ page }) => {
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await templateBtn.click();
    // Dropdown / listbox is present
    await expect(page.getByRole('listbox')).toBeVisible();
  });

  /**
   * BUG: The template dropdown renders "No data" with no selectable options.
   * This is because @nuxt/ui v4 changed the prop name from `options` to `items`,
   * but AppHeader.vue still uses `:options="templateOptions"`.
   * This test documents the bug — it should PASS once the bug is fixed.
   */
  test('template dropdown shows Modern, Classic, and Minimal options [BUG: No data]', async ({ page }) => {
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await templateBtn.click();

    await expect(page.getByRole('option', { name: 'Modern' })).toBeVisible();
    await expect(page.getByRole('option', { name: 'Classic' })).toBeVisible();
    await expect(page.getByRole('option', { name: 'Minimal' })).toBeVisible();
  });

  test('switching to Classic template via localStorage changes preview rendering', async ({ page }) => {
    // Workaround for the dropdown bug: set template via localStorage + reload
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await page.reload();

    // Classic template uses uppercase applicant name (JANE DOE)
    const previewHeading = page.getByRole('heading', { name: 'JANE DOE' });
    await expect(previewHeading).toBeVisible();

    // The Template button must reflect the persisted choice
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('classic');
  });

  test('switching to Minimal template via localStorage changes preview rendering', async ({ page }) => {
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'minimal'));
    await page.reload();

    // Preview still renders the same applicant name regardless of template
    await expect(page.getByText('Jane Doe')).toBeVisible();

    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('minimal');
  });

  test('selected template persists across reloads', async ({ page }) => {
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await page.reload();

    // Reload again
    await page.reload();
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('classic');
  });
});

// ─── 3. Document Type Selector ────────────────────────────────────────────────

test.describe('Document Type Selector', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => {
      localStorage.removeItem('ohmydoc_document_type');
    });
    await page.reload();
    const startBtn = page.getByRole('button', { name: 'Start with a sample' });
    if (await startBtn.isVisible()) {
      await startBtn.click();
      await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    }
  });

  test('header shows "Type" label with current document type button', async ({ page }) => {
    await expect(page.getByText('Type')).toBeVisible();
    // Default document type is "cover-letter"
    const typeBtn = page.getByRole('button', { name: 'Show popup' }).nth(0);
    await expect(typeBtn).toBeVisible();
    await expect(typeBtn).toContainText('cover-letter');
  });

  test('clicking Type button opens a dropdown popup', async ({ page }) => {
    const typeBtn = page.getByRole('button', { name: 'Show popup' }).nth(0);
    await typeBtn.click();
    await expect(page.getByRole('listbox')).toBeVisible();
  });

  /**
   * BUG: Same USelectMenu `options` → `items` prop issue affects the Type dropdown.
   * Currently shows "No data". Test documents expected behaviour once fixed.
   */
  test('type dropdown shows Cover Letter option [BUG: No data]', async ({ page }) => {
    const typeBtn = page.getByRole('button', { name: 'Show popup' }).nth(0);
    await typeBtn.click();
    await expect(page.getByRole('option', { name: 'Cover Letter' })).toBeVisible();
  });
});

// ─── 4. Core Editor Flow ──────────────────────────────────────────────────────

test.describe('Core Editor Flow', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    // Ensure we start with the default modern template
    await page.evaluate(() => {
      localStorage.removeItem('ohmydoc_template');
    });
    const startBtn = page.getByRole('button', { name: 'Start with a sample' });
    if (await startBtn.isVisible()) {
      await startBtn.click();
    }
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
  });

  test('left panel contains the Monaco XML editor', async ({ page }) => {
    const editor = page.getByRole('textbox', { name: 'Editor content' });
    await expect(editor).toBeVisible();
  });

  test('editor contains valid cover-letter XML', async ({ page }) => {
    // Check editor text via Monaco's underlying textarea value
    const xmlContent = await page.evaluate(() => {
      const textarea = document.querySelector('.monaco-editor textarea') as HTMLTextAreaElement | null;
      return textarea?.value ?? '';
    });
    expect(xmlContent).toContain('<applicationDocument');
    expect(xmlContent).toContain('<applicant>');
    expect(xmlContent).toContain('Jane Doe');
  });

  test('right panel shows rendered HTML preview', async ({ page }) => {
    // Preview renders the Modern template article
    await expect(page.locator('article.application-document')).toBeVisible();
  });

  test('preview renders applicant name from XML', async ({ page }) => {
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
  });

  test('preview renders all major cover-letter sections', async ({ page }) => {
    // Applicant info
    await expect(page.getByText('123 Main Street')).toBeVisible();
    await expect(page.getByText('(555) 123-4567')).toBeVisible();
    await expect(page.getByRole('link', { name: 'jane.doe@email.com' })).toBeVisible();

    // Date and recipient
    await expect(page.getByText('June 15, 2024')).toBeVisible();
    await expect(page.getByText('Hiring Manager')).toBeVisible();
    await expect(page.getByText('Brightwave Marketing')).toBeVisible();

    // Letter body
    await expect(page.getByText('Dear Hiring Manager,')).toBeVisible();
    await expect(page.getByText('GreenLeaf Promotions')).toBeVisible();
  });

  test('preview updates within ~500ms after XML edit via keyboard', async ({ page }) => {
    // Change the applicant name in the editor
    const editor = page.getByRole('textbox', { name: 'Editor content' });

    // Select all and replace with modified XML
    const modifiedXml = await page.evaluate(() => {
      const textarea = document.querySelector('.monaco-editor textarea') as HTMLTextAreaElement | null;
      const xml = textarea?.value ?? '';
      return xml.replace('<name>Jane Doe</name>', '<name>Alex Smith</name>');
    });

    await page.evaluate((xml) => {
      // Set the Monaco editor value directly
      const win = window as typeof window & { monaco?: { editor: { getModels: () => { setValue: (v: string) => void }[] } } };
      const models = win.monaco?.editor?.getModels();
      if (models && models.length > 0) {
        models[0].setValue(xml);
      }
    }, modifiedXml);

    // Wait for debounce (300ms) + some render time
    await page.waitForTimeout(600);

    // Preview should now show the new name
    await expect(page.getByRole('heading', { name: 'Alex Smith' })).toBeVisible();
  });
});

// ─── 5. Existing Functionality ────────────────────────────────────────────────

test.describe('Existing Functionality', () => {
  test('no JavaScript errors on fresh page load (welcome screen)', async ({ page }) => {
    const jsErrors: string[] = [];
    page.on('console', (msg) => {
      if (msg.type() === 'error') jsErrors.push(msg.text());
    });
    page.on('pageerror', (err) => jsErrors.push(err.message));

    await page.goto('/');
    await page.evaluate(() => {
      localStorage.removeItem('ohmydoc_xml_content');
    });
    await page.reload();
    await page.waitForLoadState('networkidle');

    expect(jsErrors).toEqual([]);
  });

  test('no JavaScript errors after loading sample', async ({ page }) => {
    const jsErrors: string[] = [];
    page.on('pageerror', (err) => jsErrors.push(err.message));

    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_xml_content'));
    await page.reload();
    await page.getByRole('button', { name: 'Start with a sample' }).click();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    await page.waitForLoadState('networkidle');

    expect(jsErrors).toEqual([]);
  });

  test('page title is correct', async ({ page }) => {
    await page.goto('/');
    await expect(page).toHaveTitle('OhMyDoc - XML to HTML Transformer');
  });

  test('header shows OhMyDoc title on welcome screen', async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_xml_content'));
    await page.reload();
    await expect(page.getByRole('heading', { name: 'OhMyDoc', level: 1 })).toBeVisible();
  });

  test('header shows OhMyDoc title on editor screen', async ({ page }) => {
    await page.goto('/');
    const startBtn = page.getByRole('button', { name: 'Start with a sample' });
    if (await startBtn.isVisible()) await startBtn.click();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    await expect(page.getByRole('heading', { name: 'OhMyDoc', level: 1 })).toBeVisible();
  });

  test('dual-panel layout is visible at 1280×800', async ({ page }) => {
    await page.setViewportSize({ width: 1280, height: 800 });
    await page.goto('/');
    const startBtn = page.getByRole('button', { name: 'Start with a sample' });
    if (await startBtn.isVisible()) await startBtn.click();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();

    // Both editor and preview must be simultaneously visible
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
    await expect(page.locator('article.application-document')).toBeVisible();
  });

  test('email link in preview is a proper mailto: link', async ({ page }) => {
    await page.goto('/');
    const startBtn = page.getByRole('button', { name: 'Start with a sample' });
    if (await startBtn.isVisible()) await startBtn.click();

