    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();

    await page.reload();
    await page.waitForLoadState('networkidle');

    // Welcome heading must NOT reappear
    await expect(
      page.getByRole('heading', { name: 'Create professional documents in seconds' }),
    ).not.toBeVisible();

    // Content is still visible
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
  });
});

// ─── 2. Template Switcher ─────────────────────────────────────────────────────

test.describe('Template Switcher', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_template'));
    await page.reload();
    await ensureSampleLoaded(page);
  });

  test('header shows "Template" label', async ({ page }) => {
    await expect(page.getByText('Template')).toBeVisible();
  });

  test('template button displays current template name (default: modern)', async ({ page }) => {
    // The second listbox button is the template switcher
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('modern');
  });

  test('clicking Template button opens a dropdown', async ({ page }) => {
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await templateBtn.click();
    await expect(page.getByRole('listbox')).toBeVisible();
  });

  /**
   * [BUG] Dropdown renders "No data" – options prop not accepted by @nuxt/ui v4.
   * Expected to FAIL until AppHeader.vue is updated to use `items` instead of `options`.
   */
  test('[BUG] template dropdown shows Modern, Classic, and Minimal options', async ({ page }) => {
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await templateBtn.click();

    await expect(page.getByRole('option', { name: 'Modern' })).toBeVisible();
    await expect(page.getByRole('option', { name: 'Classic' })).toBeVisible();
    await expect(page.getByRole('option', { name: 'Minimal' })).toBeVisible();
  });

  test('Classic template changes the preview layout (uppercase name)', async ({ page }) => {
    // Workaround for the dropdown bug: set via localStorage + reload
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await page.reload();
    await page.waitForLoadState('networkidle');

    // Classic renders applicant name in ALL CAPS
    await expect(page.getByRole('heading', { name: 'JANE DOE' })).toBeVisible();
    // Modern's article class must be absent
    await expect(page.locator('article.application-document')).not.toBeVisible();
  });

  test('Minimal template shows applicant name', async ({ page }) => {
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'minimal'));
    await page.reload();
    await page.waitForLoadState('networkidle');

    await expect(page.getByText('Jane Doe')).toBeVisible();

    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('minimal');
  });

  test('template selection persists across reloads', async ({ page }) => {
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await page.reload();
    await page.reload(); // second reload verifies persistence

    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('classic');
  });
});

// ─── 3. Document Type Selector ────────────────────────────────────────────────

test.describe('Document Type Selector', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_document_type'));
    await page.reload();
    await ensureSampleLoaded(page);
  });

  test('header shows "Type" label', async ({ page }) => {
    await expect(page.getByText('Type')).toBeVisible();
  });

  test('type button displays the current document type (default: cover-letter)', async ({ page }) => {
    const typeBtn = page.getByRole('button', { name: 'Show popup' }).nth(0);
    await expect(typeBtn).toContainText('cover-letter');
  });

  test('clicking Type button opens a dropdown', async ({ page }) => {
    const typeBtn = page.getByRole('button', { name: 'Show popup' }).nth(0);
    await typeBtn.click();
    await expect(page.getByRole('listbox')).toBeVisible();
  });

  /**
   * [BUG] Dropdown renders "No data" – same `options` → `items` issue as template switcher.
   * Expected to FAIL until AppHeader.vue is updated.
   */
  test('[BUG] type dropdown shows Cover Letter option', async ({ page }) => {
    const typeBtn = page.getByRole('button', { name: 'Show popup' }).nth(0);
    await typeBtn.click();
    await expect(page.getByRole('option', { name: 'Cover Letter' })).toBeVisible();
  });
});

// ─── 4. Core Editor Flow ──────────────────────────────────────────────────────

test.describe('Core Editor Flow', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_template'));
    await ensureSampleLoaded(page);
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
  });

  test('left panel contains the Monaco XML editor', async ({ page }) => {
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
  });

  test('editor textarea contains the XML document', async ({ page }) => {
    const xml = await page.evaluate(() => {
      const textarea = document.querySelector('.monaco-editor textarea') as HTMLTextAreaElement | null;
      return textarea?.value ?? '';
    });
    expect(xml).toContain('<applicationDocument');
    expect(xml).toContain('Jane Doe');
  });

  test('right panel shows the rendered HTML preview', async ({ page }) => {
    await expect(page.locator('article.application-document')).toBeVisible();
  });

  test('preview shows all major cover-letter sections', async ({ page }) => {
    // Applicant info
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    await expect(page.getByText('123 Main Street')).toBeVisible();
    await expect(page.getByText('(555) 123-4567')).toBeVisible();
    await expect(page.getByRole('link', { name: 'jane.doe@email.com' })).toBeVisible();

    // Date, recipient
    await expect(page.getByText('June 15, 2024')).toBeVisible();
    await expect(page.getByText('Hiring Manager')).toBeVisible();
    await expect(page.getByText('Brightwave Marketing')).toBeVisible();

    // Letter body
    await expect(page.getByText('Dear Hiring Manager,')).toBeVisible();
    await expect(page.getByText('GreenLeaf Promotions')).toBeVisible();
  });

  test('preview reflects XML changes after 300ms debounce', async ({ page }) => {
    // Modify the stored XML to use a different name, then reload to trigger full render
    const original = await page.evaluate(() => localStorage.getItem('ohmydoc_xml_content') ?? '');
    expect(original).toContain('Jane Doe');

    const modified = original.replace('<name>Jane Doe</name>', '<name>Alex Smith</name>');
    await page.evaluate((xml) => localStorage.setItem('ohmydoc_xml_content', xml), modified);

    await page.reload();
    await page.waitForLoadState('networkidle');

    await expect(page.getByRole('heading', { name: 'Alex Smith' })).toBeVisible();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).not.toBeVisible();
  });

  test('editor is interactive (can be focused)', async ({ page }) => {
    await page.locator('.monaco-editor').click();

    const editorHasFocus = await page.evaluate(() => {
      const activeEl = document.activeElement;
      return (
        activeEl?.closest('.monaco-editor') !== null
        || activeEl?.tagName === 'TEXTAREA'
      );
    });
    expect(editorHasFocus).toBe(true);
  });
});

// ─── 5. Existing Functionality ────────────────────────────────────────────────

test.describe('Existing Functionality', () => {
  test('no JS errors on welcome screen (fresh load)', async ({ page }) => {
    const jsErrors: string[] = [];
    page.on('pageerror', (err) => jsErrors.push(err.message));

    await page.goto('/');
    await clearStorage(page);
    await page.reload();
    await page.waitForLoadState('networkidle');

    expect(jsErrors).toEqual([]);
  });

  test('no JS errors after loading sample', async ({ page }) => {
    const jsErrors: string[] = [];
    page.on('pageerror', (err) => jsErrors.push(err.message));

    await page.goto('/');
    await clearStorage(page);
    await page.reload();
    await page.getByRole('button', { name: 'Start with a sample' }).click();
    await expect(page.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    await page.waitForLoadState('networkidle');

    expect(jsErrors).toEqual([]);
  });

  test('page title is "OhMyDoc - XML to HTML Transformer"', async ({ page }) => {
    await page.goto('/');
    await expect(page).toHaveTitle('OhMyDoc - XML to HTML Transformer');
  });

  test('header h1 shows "OhMyDoc"', async ({ page }) => {
    await page.goto('/');
    await expect(page.getByRole('heading', { name: 'OhMyDoc', level: 1 })).toBeVisible();
  });

  test('dual-panel layout visible at 1280×800 (no horizontal scroll)', async ({ page }) => {
    await page.setViewportSize({ width: 1280, height: 800 });
    await page.goto('/');
    await ensureSampleLoaded(page);

    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
    await expect(page.locator('article.application-document')).toBeVisible();

    const hasHScroll = await page.evaluate(
      () => document.documentElement.scrollWidth > document.documentElement.clientWidth,
    );
    expect(hasHScroll).toBe(false);
  });

  test('email link in preview is a mailto: href', async ({ page }) => {
    await page.goto('/');
    await ensureSampleLoaded(page);

    await expect(page.getByRole('link', { name: 'jane.doe@email.com' })).toHaveAttribute(
      'href',
      'mailto:jane.doe@email.com',
    );
  });

  test('Modern template uses semantic article element', async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_template'));
    await ensureSampleLoaded(page);

    await expect(page.locator('article.application-document')).toBeVisible();
  });

  test('Classic template uses different visual style from Modern', async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await ensureSampleLoaded(page);

    await expect(page.getByRole('heading', { name: 'JANE DOE' })).toBeVisible();
    await expect(page.locator('article.application-document')).not.toBeVisible();
  });
});

