    await expect(preview.getByText('Dear Hiring Manager,')).toBeVisible();
    await expect(preview.getByText('GreenLeaf Promotions')).toBeVisible();
  });

  test('preview reflects XML changes after debounce', async ({ page }) => {
    // Modify the stored XML to use a different name, then reload
    const original = await page.evaluate(() => localStorage.getItem('ohmydoc_xml_content') ?? '');
    expect(original).toContain('Jane Doe');

    const modified = original.replace('<name>Jane Doe</name>', '<name>Alex Smith</name>');
