test.describe('Core Editor Flow', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_template'));
    await ensureSampleLoaded(page);
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
  });

  test('left panel contains the Monaco XML editor', async ({ page }) => {
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
  });

  test('editor textarea reflects the loaded XML document', async ({ page }) => {
    // Monaco is loaded asynchronously; wait for it to populate
    await page.waitForFunction(
      () => {
        const ta = document.querySelector<HTMLTextAreaElement>('.monaco-editor textarea');
        return ta && ta.value.includes('applicationDocument');
      },
      { timeout: 10000 },
    );

    const xml = await page.evaluate(() => {
      const ta = document.querySelector<HTMLTextAreaElement>('.monaco-editor textarea');
      return ta?.value ?? '';
    });
    expect(xml).toContain('<applicationDocument');
    expect(xml).toContain('Jane Doe');
  });

  test('right panel shows the rendered HTML preview', async ({ page }) => {
    await expect(page.locator('article.application-document')).toBeVisible();
  });

  test('preview shows all major cover-letter sections', async ({ page }) => {
    const preview = previewPanel(page);

    // Applicant info
    await expect(preview.getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
    await expect(preview.getByText('123 Main Street')).toBeVisible();
    await expect(preview.getByText('(555) 123-4567')).toBeVisible();
    await expect(preview.getByRole('link', { name: 'jane.doe@email.com' })).toBeVisible();

    // Date, recipient
    await expect(preview.getByText('June 15, 2024')).toBeVisible();
    await expect(preview.getByText('Hiring Manager')).toBeVisible();
    await expect(preview.getByText('Brightwave Marketing')).toBeVisible();

    // Letter body
    await expect(preview.getByText('Dear Hiring Manager,')).toBeVisible();
    await expect(preview.getByText('GreenLeaf Promotions')).toBeVisible();
  });

  test('preview reflects XML changes after debounce', async ({ page }) => {
    // Modify the stored XML to use a different name, then reload
    const original = await page.evaluate(() => localStorage.getItem('ohmydoc_xml_content') ?? '');
    expect(original).toContain('Jane Doe');

    const modified = original.replace('<name>Jane Doe</name>', '<name>Alex Smith</name>');
    await page.evaluate((xml) => localStorage.setItem('ohmydoc_xml_content', xml), modified);

    await page.reload();
    await page.waitForLoadState('networkidle');

    await expect(previewPanel(page).getByRole('heading', { name: 'Alex Smith' })).toBeVisible();
    await expect(previewPanel(page).getByRole('heading', { name: 'Jane Doe' })).not.toBeVisible();
  });

  test('editor can be focused by clicking', async ({ page }) => {
    await page.locator('.monaco-editor').click();

    const editorHasFocus = await page.evaluate(() => {
      const activeEl = document.activeElement;
      return (
        activeEl?.closest('.monaco-editor') !== null
        || activeEl?.tagName === 'TEXTAREA'
      );
    });
    expect(editorHasFocus).toBe(true);
  });
});

