      .toBeVisible({ timeout: 10000 });
    // Wait for the 300ms debounce to flush XML content into localStorage
    await page.waitForFunction(
      () => !!localStorage.getItem('ohmydoc_xml_content'),
      { timeout: 5000 },
    );
  }
}

/**
 * Scoped locator for the preview panel to avoid strict-mode collisions
 * with the same text appearing in the Monaco editor.
 * Uses .first() because PreviewPanel nests two .preview-panel divs.
 */
function previewPanel(page: Page) {
  return page.locator('.preview-panel').first();
}

// ─── 1. Welcome / Onboarding Screen ──────────────────────────────────────────

test.describe('Welcome / Onboarding Screen', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await clearStorage(page);
    await page.reload();
    await page.waitForLoadState('networkidle');
  });

  test('shows welcome heading on first visit', async ({ page }) => {
    await expect(
      page.getByRole('heading', { name: 'Create professional documents in seconds', level: 2 }),
    ).toBeVisible();
  });

  test('shows tagline on welcome screen', async ({ page }) => {
    await expect(
      page.getByText('Write structured XML, pick a template, and instantly preview a polished document'),
    ).toBeVisible();
  });

  test('shows "Start with a sample" button', async ({ page }) => {
    await expect(page.getByRole('button', { name: 'Start with a sample' })).toBeVisible();
  });

  test('editor panels are hidden on welcome screen', async ({ page }) => {
    await expect(page.getByRole('textbox', { name: 'Editor content' })).not.toBeVisible();
    await expect(page.locator('article.application-document')).not.toBeVisible();
  });

  test('"Start with a sample" dismisses welcome screen and shows editor', async ({ page }) => {
    await page.getByRole('button', { name: 'Start with a sample' }).click();

    await expect(
      page.getByRole('heading', { name: 'Create professional documents in seconds' }),
    ).not.toBeVisible();
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible({ timeout: 10000 });
  });

  test('"Start with a sample" renders cover-letter preview', async ({ page }) => {
    await page.getByRole('button', { name: 'Start with a sample' }).click();
    await page.waitForLoadState('networkidle');

    await expect(previewPanel(page).getByRole('heading', { name: 'Jane Doe' })).toBeVisible({ timeout: 10000 });
    await expect(previewPanel(page).getByText('Brightwave Marketing', { exact: true }).first()).toBeVisible();
    await expect(previewPanel(page).getByText('Dear Hiring Manager,')).toBeVisible();
  });

  test('reload after "Start with a sample" skips welcome screen', async ({ page }) => {
    await page.getByRole('button', { name: 'Start with a sample' }).click();
    await expect(previewPanel(page).getByRole('heading', { name: 'Jane Doe' })).toBeVisible({ timeout: 10000 });
    // Wait for the 300ms debounce to persist XML to localStorage before reloading
    await page.waitForFunction(() => !!localStorage.getItem('ohmydoc_xml_content'), { timeout: 5000 });

    await page.reload();
    await page.waitForLoadState('networkidle');

    // Welcome heading must NOT reappear
    await expect(
      page.getByRole('heading', { name: 'Create professional documents in seconds' }),
    ).not.toBeVisible();

    // Content is still visible
    await expect(page.getByRole('textbox', { name: 'Editor content' })).toBeVisible();
    await expect(previewPanel(page).getByRole('heading', { name: 'Jane Doe' })).toBeVisible();
  });
});

// ─── 2. Template Switcher ─────────────────────────────────────────────────────

test.describe('Template Switcher', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    await page.evaluate(() => localStorage.removeItem('ohmydoc_template'));
    await page.reload();
    await ensureSampleLoaded(page);
  });

  test('header shows "Template" label', async ({ page }) => {
    await expect(page.getByText('Template')).toBeVisible();
  });

  test('template button displays current template name (default: modern)', async ({ page }) => {
    // The second listbox button is the template switcher
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('modern');
  });

  test('clicking Template button opens a dropdown', async ({ page }) => {
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await templateBtn.click();
    await expect(page.getByRole('listbox')).toBeVisible();
  });

  /**
   * [BUG] Dropdown renders "No data" – options prop not accepted by @nuxt/ui v4.
   * test.fail() marks this as a known failure. Remove test.fail() once fixed.
   */
  test('[BUG] template dropdown shows Modern, Classic, and Minimal options', async ({ page }) => {
    test.fail(true, 'BUG: USelectMenu uses `options` prop but @nuxt/ui v4 requires `items`');

    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await templateBtn.click();

    await expect(page.getByRole('option', { name: 'Modern' })).toBeVisible();
    await expect(page.getByRole('option', { name: 'Classic' })).toBeVisible();
    await expect(page.getByRole('option', { name: 'Minimal' })).toBeVisible();
  });

  test('Classic template changes the preview layout (uppercase name)', async ({ page }) => {
    // Workaround for the dropdown bug: set via localStorage + reload
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await page.reload();
    // Wait for onMounted to restore template from localStorage
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('classic', { timeout: 5000 });

    // Classic renders applicant name in ALL CAPS
    await expect(previewPanel(page).getByRole('heading', { name: 'JANE DOE' })).toBeVisible();
    // Modern's article class must be absent
    await expect(page.locator('article.application-document')).not.toBeVisible();
  });

  test('Minimal template shows applicant name', async ({ page }) => {
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'minimal'));
    await page.reload();
    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('minimal', { timeout: 5000 });

    // Use .first() — Minimal template renders "Jane Doe" in both header and signature
    await expect(previewPanel(page).getByText('Jane Doe').first()).toBeVisible();
  });

  test('template selection persists across reloads', async ({ page }) => {
    await page.evaluate(() => localStorage.setItem('ohmydoc_template', 'classic'));
    await page.reload();
    await page.reload(); // second reload verifies persistence

    const templateBtn = page.getByRole('button', { name: 'Show popup' }).nth(1);
    await expect(templateBtn).toContainText('classic', { timeout: 5000 });
  });
});

