   111→<script setup lang="ts">
   112→import { ref, computed } from 'vue'
   113→import { useTemplate } from '~/composables/useTemplate'
   114→import type { ParsedData } from '~/composables/useXmlParser'
   115→import CoverLetterModern from '~/templates/modern/CoverLetterModern.vue'
   116→import CoverLetterClassic from '~/templates/classic/CoverLetterClassic.vue'
   117→import CoverLetterMinimal from '~/templates/minimal/CoverLetterMinimal.vue'
   118→
   119→/**
   120→ * Debug Template Page
   121→ *
   122→ * This page demonstrates all available templates with comprehensive
   123→ * sample data covering all template sections and edge cases.
   124→ * Includes template selection and side-by-side comparison features.
   125→ */
   126→
   127→// Get template system active template name
   128→const { activeTemplate } = useTemplate()
   129→
   130→// View mode: 'single' or 'comparison'
   131→const viewMode = ref<'single' | 'comparison'>('single')
   132→
   133→// Selected template for single view
   134→const selectedTemplate = ref(activeTemplate)
   135→
   136→// All available templates with their components and metadata
   137→const allTemplates = [
   138→  {
   139→    name: 'modern',
   140→    component: CoverLetterModern,
   141→    metadata: {
   142→      name: 'modern',
   143→      displayName: 'Modern',
   144→      description: 'Professional cover letter template with modern styling and clean typography',
   145→    },
   146→  },
   147→  {
   148→    name: 'classic',
   149→    component: CoverLetterClassic,
   150→    metadata: {
   151→      name: 'classic',
   152→      displayName: 'Classic',
   153→      description: 'Traditional cover letter with table-based layout and formal styling',
   154→    },
   155→  },
   156→  {
   157→    name: 'minimal',
   158→    component: CoverLetterMinimal,
   159→    metadata: {
   160→      name: 'minimal',
   161→      displayName: 'Minimal',
   162→      description: 'Clean, minimalist cover letter design with simple structure and generous whitespace',
   163→    },
   164→  },
   165→]
   166→
   167→// Template options for dropdown
   168→const templateOptions = allTemplates.map(t => ({
   169→  value: t.name,
   170→  label: t.metadata.displayName,
   171→}))
   172→
   173→// Get current template component and metadata based on selection
   174→const currentTemplateComponent = computed(() => {
   175→  const template = allTemplates.find(t => t.name === selectedTemplate.value)
   176→  return template?.component || CoverLetterModern
   177→})
   178→
   179→const currentTemplateMetadata = computed(() => {
   180→  const template = allTemplates.find(t => t.name === selectedTemplate.value)
   181→  return template?.metadata || allTemplates[0].metadata
   182→})
   183→
   184→// Available template names for debug info
   185→const availableTemplates = allTemplates.map(t => t.name)
   186→
   187→/**
   188→ * Comprehensive sample data covering all template sections
   189→ *
   190→ * This data structure matches the ParsedData interface from useXmlParser.ts
   191→ * and includes edge cases like:
   192→ * - Long names
   193→ * - Multiple experience entries
   194→ * - Multiple achievements per experience
   195→ * - Various address formats
   196→ */
   197→const sampleData: ParsedData = {
   198→  formatStyle: 'modern',
   199→  applicant: {
   200→    name: 'Jane Doe',
   201→    address: {
   202→      street: '123 Main Street',
   203→      city: 'Springfield',
   204→      state: 'IL',
   205→      zipCode: '62704',
   206→    },
   207→    contactInformation: {
   208→      phone: '(555) 123-4567',
   209→      email: 'jane.doe@email.com',
   210→    },
   211→  },
   212→  date: 'June 15, 2024',
   213→  recipient: {
   214→    position: 'Hiring Manager',
   215→    company: 'Brightwave Marketing',
   216→    address: {
   217→      street: '789 Market Avenue',
   218→      city: 'Chicago',
   219→      state: 'IL',
   220→      zipCode: '60601',
   221→    },
   222→  },
   223→  letter: {
   224→    salutation: 'Dear Hiring Manager,',
   225→    introduction:
   226→      'I am writing to express my interest in the Marketing Coordinator position at Brightwave Marketing, as advertised on your company website. With a strong background in digital marketing and a passion for creative storytelling, I am excited about the opportunity to contribute to your team.',
   227→    experienceSection: [
   228→      {
   229→        employer: 'GreenLeaf Promotions',
   230→        achievements: [
   231→          { text: 'Managed multiple successful social media campaigns that increased engagement by 150%' },
   232→          { text: 'Contributed to brand development strategies for 5 major clients' },
   233→          { text: 'Coordinated promotional events with budgets exceeding $50,000' },
   234→        ],
   235→      },
   236→      {
   237→        employer: 'TechStart Solutions',
   238→        achievements: [
   239→          { text: 'Led content marketing initiatives resulting in 200% growth in organic traffic' },
   240→          { text: 'Developed and executed email marketing campaigns with 35% open rates' },
   241→          { text: 'Collaborated with cross-functional teams to launch new product lines' },
   242→        ],
   243→      },
   244→    ],
   245→    motivation:
   246→      'What excites me most about Brightwave Marketing is your commitment to creative and data-driven strategies. I thrive in dynamic environments where collaboration and innovation are valued. Your recent campaign for sustainable brands particularly resonates with my personal values and professional interests.',
   247→    closing:
   248→      'Thank you for considering my application. I welcome the opportunity to further discuss how my skills and experiences align with the needs of your company. I am available for an interview at your earliest convenience and look forward to the possibility of contributing to Brightwave Marketing\'s continued success.',
   249→    signature: 'Jane Doe',
   250→  },
   251→}
   252→
