  margin: 0 0 0 1.25rem;
  padding: 0;
  list-style-type: none;
}

.achievement {
  position: relative;
  padding-left: 1rem;
  margin: 0.2rem 0;
  line-height: var(--cr-line);
  color: var(--cr-ink);
}

.achievement::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0.6em;
  width: 5px;
  height: 5px;
  background: var(--cr-accent);
  border-radius: 50%;
}

.signature {
  font-weight: 700;
  color: var(--cr-sidebar-bg);
  margin-top: 1.5rem;
}

