@media print {
  .application-document {
    max-width: none;
  }

  .sidebar {
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
  }

  .contact-item.email {
    text-decoration: none;
  }
}

/* ── Responsive: stack on mobile ────────────────────────── */

@media screen and (max-width: 640px) {
  .application-document {
    grid-template-columns: 1fr;
    min-height: unset;
  }

  .sidebar {
    padding: 1.5rem 1.25rem;
  }

  .applicant-name {
    font-size: 1.3rem;
  }

  .contact-information {
    flex-direction: row;
    flex-wrap: wrap;
    gap: 0.5rem;
    padding-top: 0.75rem;
    margin-top: 0.75rem;
  }

  .main-content {
    padding: 1.5rem 1.25rem;
  }
}

