/**
 * Executive Template Styles
 *
 * Bold, confident, professional cover letter template.
 * Serif typography with a navy accent color, strong name treatment,
 * and clean horizontal rules as section dividers.
 */

:root {
  --exec-font-body: Georgia, "Times New Roman", Times, serif;
  --exec-font-ui: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  --exec-max-width: 720px;
  --exec-ink: #0d0d0d;
  --exec-muted: #4a4a4a;
  --exec-accent: #1a3a5c;
  --exec-rule: #1a3a5c;
  --exec-gap: 1rem;
  --exec-gap-lg: 1.5rem;
  --exec-line: 1.65;
}

* {
  box-sizing: border-box;
}

.application-document {
  margin: 0 auto;
  max-width: var(--exec-max-width);
  background: #fff;
  color: var(--exec-ink);
  font: 16px/var(--exec-line) var(--exec-font-body);
  padding: 2.5rem 1.5rem;
}

/* ── Header ─────────────────────────────────────────────── */

.application-header {
  margin-bottom: 1.25rem;
}

.applicant-name {
  font-family: var(--exec-font-body);
  font-size: 2.4rem;
  font-weight: 700;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: var(--exec-accent);
  margin: 0 0 0.6rem 0;
  line-height: 1.1;
}

.header-meta {
  display: flex;
  flex-wrap: wrap;
  align-items: baseline;
  gap: 0 1rem;
  font-family: var(--exec-font-ui);
  font-size: 0.88rem;
  color: var(--exec-muted);
}

.applicant-address {
  font-style: normal;
  margin: 0;
}

.contact-information {
  margin: 0;
}

.contact-information .separator {
  color: var(--exec-muted);
}

.contact-information .email {
  color: inherit;
  text-decoration: none;
  border-bottom: 1px dotted currentColor;
}

.contact-information .email:hover {
  color: var(--exec-accent);
}

/* ── Horizontal Rule Dividers ────────────────────────────── */

.rule {
  border: none;
  border-top: 1.5px solid var(--exec-rule);
  margin: 1.25rem 0;
}

/* ── Date & Recipient ─────────────────────────────────────── */

.pre-letter {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-bottom: 0.25rem;
}

.document-date {
  font-family: var(--exec-font-ui);
  font-size: 0.9rem;
  color: var(--exec-muted);
  text-align: right;
}

.recipient {
  margin-top: 0.25rem;
}

.recipient-position {
  margin: 0 0 0.1rem 0;
  font-weight: 700;
  color: var(--exec-ink);
}

.recipient-company {
  margin: 0 0 0.2rem 0;
  font-family: var(--exec-font-ui);
  font-size: 0.95rem;
  color: var(--exec-ink);
}

.recipient-address {
  font-style: normal;
  color: var(--exec-muted);
  font-size: 0.9rem;
  line-height: 1.5;
  margin: 0;
}

/* ── Letter Body ─────────────────────────────────────────── */

.letter {
  margin-top: 0.25rem;
}

.letter > p {
  margin: 0 0 1.1rem 0;
  line-height: var(--exec-line);
}

.salutation {
  font-weight: 600;
  margin-bottom: 1rem;
}

.experience-section {
  margin: 1rem 0;
}

.experience {
  margin-bottom: 1rem;
}

.experience-employer {
  margin: 0 0 0.3rem 0;
}

.experience-employer strong {
  font-weight: 700;
  color: var(--exec-accent);
  font-style: italic;
}

.achievements {
  margin: 0.25rem 0 0.75rem 1.5rem;
  padding: 0;
  list-style-type: square;
}

.achievement {
  margin: 0.25rem 0;
  line-height: var(--exec-line);
  color: var(--exec-ink);
}

.signature {
  margin-top: 2.75rem;
  font-weight: 700;
  font-size: 1.05rem;
  color: var(--exec-accent);
  letter-spacing: 0.02em;
}

/* ── Print ───────────────────────────────────────────────── */

@media print {
  .application-document {
    padding: 0;
    max-width: none;
    margin: 0 2cm;
  }

  .contact-information .email {
    color: inherit;
    border-bottom: none;
  }
}

