@media screen and (max-width: 768px) {
  .application-document {
    padding: 1.5rem 1rem;
  }

  .applicant-name {
    font-size: 1.8rem;
  }

  .header-meta {
    flex-direction: column;
    gap: 0.2rem;
  }

  .document-date {
    text-align: left;
