/**
 * Brief Resignation Letter Template Styles
 *
 * Compact, minimal design — just the essentials.
 * Single-line header, one-line recipient, plain paragraphs, short signature.
 *
 * Color palette: --ink: #111, --muted: #555  (project design system)
 * Typography:    --font-body: ui-serif (document body), --font-ui: ui-sans-serif (header/meta)
 */

/* ── Design System ───────────────────────────────────────────────────── */
:root {
  --rb-font-body: ui-serif, Georgia, "Times New Roman", Times, serif;
  --rb-font-ui:   ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  --rb-ink:       #111;
  --rb-muted:     #666;
  --rb-max-width: 640px;
  --rb-line:      1.6;
}

/* ── Document Container ──────────────────────────────────────────────── */
.resignation-brief {
  margin: 0 auto;
  max-width: var(--rb-max-width);
  background: #fff;
  color: var(--rb-ink);
  font: 15px/var(--rb-line) var(--rb-font-body);
  padding: 2rem 1rem;
}

/* ── Header Row ─────────────────────────────────────────────────────── */
.rb-header {
  display: flex;
  flex-wrap: wrap;
  align-items: baseline;
  gap: 0.4rem;
  font-family: var(--rb-font-ui);
  font-size: 0.85rem;
  color: var(--rb-muted);
  margin-bottom: 0.35rem;
}

.rb-name {
  font-weight: 600;
  color: var(--rb-ink);
  font-size: 1rem;
}

.rb-position {
  color: var(--rb-muted);
}

/* Date pushed to the right */
.rb-date {
  margin-left: auto;
  white-space: nowrap;
}

.rb-sep {
  color: var(--rb-muted);
  opacity: 0.5;
  user-select: none;
}

/* ── Recipient Line ──────────────────────────────────────────────────── */
.rb-recipient {
  display: flex;
  flex-wrap: wrap;
  align-items: baseline;
  gap: 0.4rem;
  font-family: var(--rb-font-ui);
  font-size: 0.82rem;
  color: var(--rb-muted);
  margin-bottom: 1.5rem;
  padding-bottom: 0.75rem;
  border-bottom: 1px solid #e5e5e5;
}

/* ── Body Paragraphs ────────────────────────────────────────────────── */
.rb-body {
  margin-bottom: 1.75rem;
}

.rb-paragraph {
  margin: 0 0 0.85rem 0;
}

.rb-paragraph:last-child {
  margin-bottom: 0;
}

/* ── Signature ──────────────────────────────────────────────────────── */
.rb-signature {
  margin-top: 1.75rem;
  font-family: var(--rb-font-ui);
  font-size: 0.9rem;
  font-weight: 600;
  color: var(--rb-ink);
}

/* ── Print ──────────────────────────────────────────────────────────── */
@media print {
  .resignation-brief {
    padding: 0;
    max-width: none;
    margin: 0 2cm;
  }
}

/* ── Responsive ─────────────────────────────────────────────────────── */
@media screen and (max-width: 600px) {
  .resignation-brief {
    padding: 1rem 0.75rem;
  }

  .rb-date {
    margin-left: 0;
    width: 100%;
  }
}

