import { test, expect } from '@playwright/test';

/**
 * Smoke Tests - v2.0 Navigation
 * Validated with agent-browser CLI before writing
 */
test.describe('Smoke: Navigation @smoke', () => {

  test('should load home page', async ({ page }) => {
    await page.goto('/');
    await expect(page).toHaveURL('/');
    // Verified: pot switcher and quick actions visible
    await expect(page.getByRole('button', { name: /our pot/i })).toBeVisible();
  });

  test('should navigate to transactions', async ({ page }) => {
    await page.goto('/');
    await page.getByRole('link', { name: /transactions/i }).click();
    await expect(page).toHaveURL('/transactions');
    // Verified: month selector visible (current month will be highlighted)
    await expect(page.getByText(/Spent/i)).toBeVisible();
  });

  test('should navigate to agent', async ({ page }) => {
    await page.goto('/');
    await page.getByRole('link', { name: /agent/i }).click();
    await expect(page).toHaveURL('/agent');
    // Verified: Direct Entry tab visible
    await expect(page.getByRole('button', { name: /direct entry/i })).toBeVisible();
  });

  test('should navigate to settings', async ({ page }) => {
    await page.goto('/');
    await page.getByRole('link', { name: /settings/i }).click();
    await expect(page).toHaveURL('/settings');
    // Verified: Pot Settings heading visible
    await expect(page.getByRole('heading', { name: /pot settings/i })).toBeVisible();
  });
});

test.describe('Smoke: Bottom Navigation @smoke', () => {

  test('should display all nav items', async ({ page }) => {
    await page.goto('/');
    await expect(page.getByRole('link', { name: /home/i })).toBeVisible();
    await expect(page.getByRole('link', { name: /transactions/i })).toBeVisible();
    await expect(page.getByRole('link', { name: /agent/i })).toBeVisible();
    await expect(page.getByRole('link', { name: /settings/i })).toBeVisible();
  });
});

