import { test, expect } from '@playwright/test';

/**
 * Smoke Tests - v2.0 Stitch Design Elements
 * Validated with agent-browser CLI
 */
test.describe('Smoke: Home Page Elements @smoke', () => {

  test('should display spending insight card', async ({ page }) => {
    await page.goto('/');
    await expect(page.getByRole('heading', { name: /spending insight/i })).toBeVisible();
  });

  test('should display quick action buttons', async ({ page }) => {
    await page.goto('/');
    await expect(page.getByRole('button', { name: /add funds/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /expense/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /voice/i })).toBeVisible();
  });

  test('should display members section', async ({ page }) => {
    await page.goto('/');
    await expect(page.getByRole('heading', { name: 'Members', exact: true })).toBeVisible();
  });

  test('should display recent activity section', async ({ page }) => {
    await page.goto('/');
    await expect(page.getByRole('heading', { name: /recent activity/i })).toBeVisible();
  });
});

test.describe('Smoke: Transactions Page Elements @smoke', () => {

  test('should display month selector', async ({ page }) => {
    await page.goto('/transactions');
    // Month navigation buttons visible
    await expect(page.getByRole('button', { name: /previous month/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /next month/i })).toBeVisible();
  });

  test('should display summary stats', async ({ page }) => {
    await page.goto('/transactions');
    await expect(page.getByText(/Spent/i)).toBeVisible();
    await expect(page.getByText(/Funded/i)).toBeVisible();
    await expect(page.getByText(/Balance/i)).toBeVisible();
  });

  test('should display empty state with CTA', async ({ page }) => {
    await page.goto('/transactions');
    await expect(page.getByRole('heading', { name: /no transactions yet/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /add expense/i })).toBeVisible();
  });
});

test.describe('Smoke: Agent Page Elements @smoke', () => {

  test('should display agent tabs', async ({ page }) => {
    await page.goto('/agent');
    await expect(page.getByRole('button', { name: /direct entry/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /chat/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /budget/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /insights/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /scan/i })).toBeVisible();
  });

  test('should display smart input', async ({ page }) => {
    await page.goto('/agent');
    await expect(page.getByPlaceholder(/paste notes/i)).toBeVisible();
  });

  test('should display example prompts', async ({ page }) => {
    await page.goto('/agent');
    await expect(page.getByRole('button', { name: /coffee and taxi/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /groceries/i })).toBeVisible();
  });

  test('should display parse button disabled initially', async ({ page }) => {
    await page.goto('/agent');
    const parseBtn = page.getByRole('button', { name: /parse/i });
    await expect(parseBtn).toBeVisible();
    await expect(parseBtn).toBeDisabled();
  });
});

test.describe('Smoke: Settings Page Elements @smoke', () => {

  test('should display pot settings section', async ({ page }) => {
    await page.goto('/settings');
    await expect(page.getByRole('heading', { name: /pot settings/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /manage members/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /categories/i })).toBeVisible();
  });

  test('should display data management section', async ({ page }) => {
    await page.goto('/settings');
    await expect(page.getByRole('heading', { name: /data management/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /backup database/i })).toBeVisible();
    await expect(page.getByRole('button', { name: /restore from backup/i })).toBeVisible();
  });

  test('should display coming soon features', async ({ page }) => {
    await page.goto('/settings');
    await expect(page.getByText(/push notifications/i)).toBeVisible();
    await expect(page.getByText(/coming soon/i)).toBeVisible();
  });

  test('should display dark mode toggle', async ({ page }) => {
    await page.goto('/settings');
    await expect(page.getByRole('heading', { name: /appearance/i })).toBeVisible();
    await expect(page.getByRole('switch')).toBeVisible();
  });
});
